  twitter: "text-blue-400",
};

export default function RecentPosts() {
  const { data: posts, isLoading } = useQuery({
    queryKey: ["/api/posts"],
  });

  if (isLoading) {
    return (
      <Card className="shadow-sm border border-gray-200">
        <CardHeader>
          <CardTitle>Recent Posts</CardTitle>
